import React, { useState, useEffect } from "react";
import {
  Card,
  CardHeader,
  CardBody,
  CardFooter,
  Flex,
  Heading,
  Box,
  Text,
  Container,
  Button,
  Center
} from "@chakra-ui/react";
import { Link,useNavigate } from "react-router-dom";
function App() {
  const [questions, setQuestions] = useState([]);
  const [userAnswers, setUserAnswers] = useState([]);
  const [score, setScore] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [profiledata, setProfiledata] = useState()
  const navigate = useNavigate()

  useEffect(() => {
    generateQuestions();
    // getprofilequestions()

  }, []);


  const getprofilequestions = async () => {

    fetch("http://localhost:8000/api/getprofile/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ username: localStorage.getItem("username") }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        console.log(data)
        setProfiledata(data);
        console.log(profiledata)
        // setShowResult(true);
      })
      .catch((error) => {
        console.error("Error:", error);
        // setScore(0);
        // setShowResult(true);
      });
  };

  const generateQuestions = () => {
    fetch("http://localhost:8000/api/generate/")
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        console.log(data)
        setQuestions(data)
      })
      .catch((error) => {
        console.error("Error:", error);
        setQuestions([]);
      });
    console.log(questions)
  };

  const handleAnswerSelect = (questionId, selectedChoice) => {
    const answer = { question_id: questionId, selected_choice: selectedChoice };
    setUserAnswers((prevAnswers) => [...prevAnswers, answer]);

    const question = questions.find((q) => q.id === questionId);
    const isCorrect = selectedChoice === question.correct_choice;

    if (isCorrect) {
      console.log("CORRECT ANSWER ðŸ¤©ðŸ¤©ðŸ¤©");

      alert("CORRECT ANSWER ðŸ¤©ðŸ¤©ðŸ¤©");
    } else {
      alert("WRONG ANSWER ðŸ¤”ðŸ¤”ðŸ¤”");
    }
  };

  const submitAnswers = () => {
    fetch("http://localhost:8000/api/submit/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ user_answers: userAnswers }),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setScore(data.score);
        setShowResult(true);
      })
      .catch((error) => {
        console.error("Error:", error);
        setScore(0);
        setShowResult(true);
      });
  };




  const handledashboardinsert = async (e) => {
    e.preventDefault();

    const username = localStorage.getItem("username")

    const datas = {
      usernameuser: username,
      question1: questions[0].question_text,
      question2: questions[1].question_text,
      question3: questions[2].question_text,
      question4: questions[3].question_text,
      question5: questions[4].question_text,
      scoredata: score



    };

    try {
      const response = await fetch("http://localhost:8000/api/inserttoprofile/", {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(datas),
      });

      if (response.ok) {
        console.log('questions added successfully!');
        navigate("/profile")

      } else {
        console.error('Error adding item:', response.statusText);
      }
    } catch (error) {
      console.error('Error adding item:', error);
    }
  };

  return (
    <div className="">
      {showResult ? (
        <Box mt={10}>
          {/* <Heading mt={5}></Heading> */}

          <Container mt={10}>
            <Card>
              <CardHeader>
                <Heading size="md" fontSize={"3xl"} mt={5}>
                  Result Summary
                </Heading>
              </CardHeader>

              <CardBody>
                <Text fontSize={"2xl"}>
                  {" "}
                  Correct Answers: {score} / {questions.length}
                </Text>
              </CardBody>
              <CardBody>
                <Button onClick={handledashboardinsert}>
                  Save

                </Button>



              </CardBody>
            </Card>
          </Container>
        </Box>
      ) : (
        <Box>
          {questions.map((question) => (
            <Box key={question.id} >
              <Container maxW={"4xl"}>
              <Card mt={10} mb={10} maxW={"3xl"}>
                <CardHeader>
                  <Heading textAlign={"center"} size="md">{question.question_text}</Heading>
                </CardHeader>

                <CardBody>
                  <Flex
                    align={"center"}
                    justifyContent={"space-between"}
                    spacing="4"
                  >
                    <Box mb={5}>
                      <Button
                        onClick={() =>
                          handleAnswerSelect(question.id, question.choice1)
                        }
                      >
                        1. {question.choice1}
                      </Button>
                    </Box>
                    <Box mb={5}>
                      <Button
                        onClick={() =>
                          handleAnswerSelect(question.id, question.choice2)
                        }
                      >
                        2. {question.choice2}
                      </Button>
                    </Box>
                  </Flex>
                  <Flex
                    align={"center"}
                    justifyContent={"space-between"}
                    spacing="4"
                  >
                    <Box mb={5}>
                      <Button
                        onClick={() =>
                          handleAnswerSelect(question.id, question.choice3)
                        }
                      >
                        3. {question.choice3}
                      </Button>
                    </Box>
                    <Box mb={5}>
                      <Button
                        onClick={() =>
                          handleAnswerSelect(question.id, question.choice4)
                        }
                      >
                        4. {question.choice4}
                      </Button>
                    </Box>
                  </Flex>
                </CardBody>
              </Card>
              </Container>

            </Box>
          ))}
          <Container mb={20}  maxW={"4xl"}>
            <Button width={"3xl"} colorScheme="blue" onClick={submitAnswers}>
              Get Summary
            </Button>
          </Container>
        </Box>
      )}
    </div>
  );
}

export default App;
